function ajax_roller(trg,i,max,is_lock,user){
	if(i <= max-1){
		var trg2=$(trg+" option:eq("+i+")");
		if(trg2){
			ajax_ban(trg,is_lock,user,trg2.text());
			trg2.delay(5000);
			i++;
			ajax_roller(trg,i,$(trg+" option").length,is_lock,user);
		}
	}
}

