fis.config.merge({
    roadmap : {
        path : [
            {
                //所有widget目录下的js文件
                reg : /^/(js2|js|vendor)/(.*.js)/i,
                //是模块化的js文件（标记为这种值的文件，会进行amd或者闭包包装）
                isMod : false,
                //默认依赖lib.js
                //requires : [ 'lib.js' ],
                //向产出的map.json文件里附加一些信息
                //extras : { author : 'Adam' },
                //编译后产出到 /static/widget/xxx 目录下
                release : '/$&'
            },
			{
                //所有widget目录下的js文件
                reg : /^/(css|vendor)/(.*.)(css)/i,
                //requires : [ 'base.css' ],
                //向产出的map.json文件里附加一些信息
                //extras : { author : 'Adam' },
                //编译后产出到 /static/widget/xxx 目录下
                release : '/$&'
            },
			{
                //所有image目录下的.png，.gif文件
                reg : /^/(css|img|vendor)/(.*).(?:jpg|jpeg|png|gif)/i,
                //访问这些图片的url是 '/m/xxxx?log_id=123'
                //url : '/m/$1?log_id=123',
                //发布到/static/pic/xxx目录下
                release : '/$&'
            },
			{
                reg : /^/(css|img|vendor)/(.*).(?:eot|ttf|woff|svg)/i,
                //访问这些图片的url是 '/m/xxxx?log_id=123'
                //url : '/m/$1?log_id=123',
                release : '/$&'
            },
            {
                //前面规则未匹配到的其他文件
                reg : /.*/,
                //编译的时候不要产出了
                release : false
            }
        ]
    }
});


